import React from 'react'
import styled, { ThemeProvider } from 'styled-components'
import {lightTheme} from './Themes';
import { Design, Develope} from './AllSvgs';
import {GiArtificialIntelligence} from 'react-icons/gi'

import LogoComponent from '../subComponents/LogoComponent';
import SocialIcons from '../subComponents/SocialIcons';
import PowerButton from '../subComponents/PowerButton';
import ParticleComponent from '../subComponents/ParticleComponent';
import BigTitle from '../subComponents/BigTitle'

const Box = styled.div`
background-color: ${props => props.theme.body};
width: 100vw;
height:100vh;
position: relative;
display: flex;
justify-content: space-evenly;
align-items: center;
`

const Main = styled.div`
border: 2px solid ${props => props.theme.text};
color: ${props => props.theme.text};
background-color: ${props => props.theme.body};
padding: 2rem;
width: 30vw;
height: 70vh;
z-index:3;
line-height: 1.5;
cursor: text;
font-family: 'Ubuntu Mono',monospace;
display: flex;
flex-direction: column;
justify-content: space-between;
&:hover{
    color: ${props => props.theme.body};
    background-color: ${props => props.theme.text};
}
`

const Title = styled.h2`
display: flex;
justify-content: center;
align-items: center;
font-size: calc(1em + 1vw);
${Main}:hover &{
    &>*{
        fill:${props => props.theme.body};
    }
}
&>*:first-child{
margin-right: 1rem;
}
`

const Description = styled.div`
color: ${props => props.theme.text};
font-size: calc(0.6em + 1vw);
padding: 0.5rem 0;
${Main}:hover &{
   
        color:${props => props.theme.body};
    
}
strong{
    margin-bottom: 1rem;
    text-transform: uppercase;
}
ul,p{
    margin-left: 2rem;
}
`

const MySkillsPage = () => {
    return (
        <ThemeProvider theme={lightTheme}>
<Box>

<LogoComponent theme='light'/>
<SocialIcons theme='light'/>
<PowerButton />
<ParticleComponent theme='light' />
            <Main>
<Title>
    <GiArtificialIntelligence size={40} /> Artificial Intelligence
</Title>
<Description>
After having my bachelor's minor in Image Processing, I chose my thesis in employing CNNs on the classification of Covid-19 X-rays. 
</Description>
<Description>
<strong>Technical Skills</strong>
<ul>
    <li>
        PyTorch
    </li>
    <li>
        Numpy
    </li>
    <li>
        Pandas
    </li>
    <li>
        Data Modeling
    </li>
    <li>
        Data Cleaning
    </li>
</ul>
</Description>


            </Main>
            <Main>
<Title>
    <Develope width={40} height={40} /> Software Developer
</Title>
<Description>
I had the privilege to spend 2 of my internships in Software Development 
</Description>
<Description>
<strong>Skills</strong>
<p>
Python, Django, JS, HTML, CSS, React, Redux, REST APIs, PHP
</p>
</Description>
<Description>
<strong>Tools</strong>
<p>
VScode, Github, MySQL, Jest, Docker
</p>
</Description>

            </Main>

            <BigTitle text="SKILLS" top="80%" right="30%" />

        </Box>

        </ThemeProvider>
        
    )
}

export default MySkillsPage
